package uk.gov.di.ipv.cri.common.library.service;

import com.nimbusds.jose.JOSEException;
import com.nimbusds.jose.JWSAlgorithm;
import com.nimbusds.jose.JWSHeader;
import com.nimbusds.jose.crypto.ECDSASigner;
import com.nimbusds.jose.crypto.RSASSASigner;
import com.nimbusds.jose.jwk.ECKey;
import com.nimbusds.jwt.JWTClaimsSet;
import com.nimbusds.jwt.SignedJWT;
import com.nimbusds.oauth2.sdk.id.ClientID;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.extension.ExtendWith;
import org.mockito.junit.jupiter.MockitoExtension;
import uk.gov.di.ipv.cri.common.library.exception.SessionValidationException;

import java.security.KeyFactory;
import java.security.NoSuchAlgorithmException;
import java.security.interfaces.RSAPrivateKey;
import java.security.spec.InvalidKeySpecException;
import java.security.spec.PKCS8EncodedKeySpec;
import java.text.ParseException;
import java.time.Instant;
import java.time.ZoneId;
import java.time.temporal.ChronoUnit;
import java.util.Base64;
import java.util.Date;
import java.util.Map;
import java.util.UUID;

import static org.junit.jupiter.api.Assertions.assertDoesNotThrow;
import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertThrows;

@ExtendWith(MockitoExtension.class)
class JWTVerifierTest {
    private static final ClientID CLIENT_ID = new ClientID("ipv-core-stub");
    private JWTVerifier jwtVerifier;
    private Instant now = Instant.now();

    private final String wrongPublicCertificate =
            "";

    private final String privateKey =
            "";

    private final String publicCertificate =
            "";

    private final String publicSigningJwkBase64 =
            "";

    private final String privateSigningJwkBase64 =
            "";

    @BeforeEach
    void setup() {
        jwtVerifier = new JWTVerifier();
    }

    @Test
    void shouldThrowValidationExceptionWhenJWTHeaderDoesNotMatchConfig() {
        Map<String, String> clientConfigMap = Map.of("authenticationAlg", "RS256");
        SignedJWT signedJWT =
                new SignedJWT(
                        new JWSHeader.Builder(JWSAlgorithm.RS512).build(),
                        new JWTClaimsSet.Builder().build());

        SessionValidationException exception =
                assertThrows(
                        SessionValidationException.class,
                        () ->
                                jwtVerifier.verifyAccessTokenJWT(
                                        clientConfigMap, signedJWT, CLIENT_ID));
        assertEquals(
                "jwt signing algorithm RS512 does not match signing algorithm configured for client: RS256",
                exception.getMessage());
    }

    @Test
    void shouldThrowValidationExceptionWhenJWTClaimsSetIssuerDoesNotMatchClientId() {
        Map<String, String> clientConfigMap = getSSMClientConfig();

        SignedJWT signedJWT =
                new SignedJWT(
                        new JWSHeader.Builder(JWSAlgorithm.RS256).build(),
                        new JWTClaimsSet.Builder()
                                .jwtID(UUID.randomUUID().toString())
                                .issuer("incorrect-issuer-url")
                                .notBeforeTime(Date.from(now))
                                .audience("https://address.cri.account.gov.uk")
                                .subject(CLIENT_ID.getValue())
                                .expirationTime(Date.from(now.plus(1, ChronoUnit.HOURS)))
                                .build());

        SessionValidationException exception =
                assertThrows(
                        SessionValidationException.class,
                        () ->
                                jwtVerifier.verifyAccessTokenJWT(
                                        clientConfigMap, signedJWT, CLIENT_ID));
        assertEquals(
                "JWT iss claim has value incorrect-issuer-url, must be ipv-core-stub",
                exception.getMessage());
    }

    @Test
    void shouldThrowValidationExceptionWhenJWTClaimsExpiryExceedsLimit() {
        SessionValidationException exception =
                assertThrows(
                        SessionValidationException.class,
                        () ->
                                jwtVerifier.validateMaxAllowedJarTtl(
                                        Instant.now()
                                                .plusSeconds(101)
                                                .atZone(ZoneId.of("UTC"))
                                                .toInstant(),
                                        100));
        assertEquals(
                "The client JWT expiry date has surpassed the maximum allowed ttl value",
                exception.getMessage());
    }

    @Test
    void shouldNotThrowValidationExceptionWhenJWTClaimsWithinLimit() {
        jwtVerifier.validateMaxAllowedJarTtl(
                Instant.now().plusSeconds(99).atZone(ZoneId.of("UTC")).toInstant(), 100);
    }

    @Test
    void shouldThrowValidationExceptionWhenIssuerExpNbfAudSubAreNotPresent() {
        Map<String, String> clientConfigMap = getSSMClientConfig();
        SignedJWT signedJWT =
                new SignedJWT(
                        new JWSHeader.Builder(JWSAlgorithm.RS256).build(),
                        new JWTClaimsSet.Builder().build());

        SessionValidationException exception =
                assertThrows(
                        SessionValidationException.class,
                        () ->
                                jwtVerifier.verifyAccessTokenJWT(
                                        clientConfigMap, signedJWT, CLIENT_ID));
        assertEquals(
                "JWT missing required claims: [aud, exp, iss, jti, sub]", exception.getMessage());
    }

    @Test
    void shouldThrowValidationExceptionWhenClientX509CertDoesNotMatchPrivateKey()
            throws JOSEException, InvalidKeySpecException, NoSuchAlgorithmException {
        Map<String, String> clientConfigMap =
                Map.of(
                        "issuer",
                        "https://dev.core.ipv.account.gov.uk",
                        "publicSigningJwkBase64",
                        wrongPublicCertificate,
                        "authenticationAlg",
                        "RS256",
                        "audience",
                        "https://address.cri.account.gov.uk");

        SignedJWT signedJWT =
                new SignedJWT(
                        new JWSHeader.Builder(JWSAlgorithm.RS256).build(),
                        new JWTClaimsSet.Builder()
                                .jwtID(UUID.randomUUID().toString())
                                .issuer(CLIENT_ID.getValue())
                                .notBeforeTime(Date.from(now))
                                .audience("https://address.cri.account.gov.uk")
                                .subject(CLIENT_ID.getValue())
                                .expirationTime(Date.from(now.plus(1, ChronoUnit.HOURS)))
                                .build());

        RSASSASigner rsaSigner = new RSASSASigner(getPrivateKey());
        signedJWT.sign(rsaSigner);

        SessionValidationException exception =
                assertThrows(
                        SessionValidationException.class,
                        () ->
                                jwtVerifier.verifyAccessTokenJWT(
                                        clientConfigMap, signedJWT, CLIENT_ID));
        assertEquals("JWT signature verification failed", exception.getMessage());
    }

    @Test
    void shouldValidateJWTSignedWithECKey() throws JOSEException, ParseException {
        Map<String, String> clientConfigMap = getECSSMClientConfig();
        SignedJWT signedJWT =
                new SignedJWT(
                        new JWSHeader.Builder(JWSAlgorithm.ES256).build(),
                        new JWTClaimsSet.Builder()
                                .jwtID(UUID.randomUUID().toString())
                                .issuer(CLIENT_ID.getValue())
                                .notBeforeTime(Date.from(now))
                                .audience("https://address.cri.account.gov.uk")
                                .subject(CLIENT_ID.getValue())
                                .expirationTime(Date.from(now.plus(1, ChronoUnit.HOURS)))
                                .build());

        ECDSASigner ecdsaSigner = new ECDSASigner(getECPrivateKey());

        signedJWT.sign(ecdsaSigner);

        assertDoesNotThrow(
                () -> jwtVerifier.verifyAccessTokenJWT(clientConfigMap, signedJWT, CLIENT_ID));
    }

    @Test
    void shouldThrowValidationExceptionWhenJWTIsExpired()
            throws InvalidKeySpecException, NoSuchAlgorithmException, JOSEException {
        Map<String, String> clientConfigMap = getSSMClientConfig();
        SignedJWT signedJWT =
                new SignedJWT(
                        new JWSHeader.Builder(JWSAlgorithm.RS256).build(),
                        new JWTClaimsSet.Builder()
                                .jwtID(UUID.randomUUID().toString())
                                .issuer(CLIENT_ID.getValue())
                                .notBeforeTime(Date.from(now))
                                .subject(CLIENT_ID.getValue())
                                .audience("https://address.cri.account.gov.uk")
                                .expirationTime(Date.from(now.minus(1, ChronoUnit.HOURS)))
                                .build());
        RSASSASigner rsaSigner = new RSASSASigner(getPrivateKey());
        signedJWT.sign(rsaSigner);

        SessionValidationException exception =
                assertThrows(
                        SessionValidationException.class,
                        () ->
                                jwtVerifier.verifyAccessTokenJWT(
                                        clientConfigMap, signedJWT, CLIENT_ID));

        assertEquals("Expired JWT", exception.getMessage());
    }

    @Test
    void shouldThrowValidationExceptionWhenAudienceDoesNotMatchConfig()
            throws InvalidKeySpecException, NoSuchAlgorithmException, JOSEException {
        Map<String, String> clientConfigMap = getSSMClientConfig();
        SignedJWT signedJWT =
                new SignedJWT(
                        new JWSHeader.Builder(JWSAlgorithm.RS256).build(),
                        new JWTClaimsSet.Builder()
                                .jwtID(UUID.randomUUID().toString())
                                .issuer(CLIENT_ID.getValue())
                                .notBeforeTime(Date.from(now))
                                .audience("incorrect-audience")
                                .subject(CLIENT_ID.getValue())
                                .expirationTime(Date.from(now.plus(1, ChronoUnit.HOURS)))
                                .build());
        RSASSASigner rsaSigner = new RSASSASigner(getPrivateKey());
        signedJWT.sign(rsaSigner);

        SessionValidationException exception =
                assertThrows(
                        SessionValidationException.class,
                        () ->
                                jwtVerifier.verifyAccessTokenJWT(
                                        clientConfigMap, signedJWT, CLIENT_ID));

        assertEquals(
                "JWT aud claim has value [incorrect-audience], must be [https://address.cri.account.gov.uk]",
                exception.getMessage());
    }

    @Test
    void shouldThrowValidationExceptionWhenJWTNotBeforeTimeIsInThePast()
            throws InvalidKeySpecException, NoSuchAlgorithmException, JOSEException {
        Map<String, String> clientConfigMap = getSSMClientConfig();
        SignedJWT signedJWT =
                new SignedJWT(
                        new JWSHeader.Builder(JWSAlgorithm.RS256).build(),
                        new JWTClaimsSet.Builder()
                                .jwtID(UUID.randomUUID().toString())
                                .issuer(CLIENT_ID.getValue())
                                .notBeforeTime(Date.from(now.plus(6, ChronoUnit.HOURS)))
                                .subject(CLIENT_ID.getValue())
                                .audience("https://address.cri.account.gov.uk")
                                .expirationTime(Date.from(now.plus(1, ChronoUnit.HOURS)))
                                .build());
        RSASSASigner rsaSigner = new RSASSASigner(getPrivateKey());
        signedJWT.sign(rsaSigner);

        SessionValidationException exception =
                assertThrows(
                        SessionValidationException.class,
                        () ->
                                jwtVerifier.verifyAccessTokenJWT(
                                        clientConfigMap, signedJWT, CLIENT_ID));

        assertEquals("JWT before use time", exception.getMessage());
    }

    @Test
    void shouldThrowValidationExceptionWhenMissingNBF()
            throws InvalidKeySpecException, NoSuchAlgorithmException, JOSEException {
        Map<String, String> clientConfigMap = getSSMClientConfig();
        SignedJWT signedJWT =
                new SignedJWT(
                        new JWSHeader.Builder(JWSAlgorithm.RS256).build(),
                        new JWTClaimsSet.Builder()
                                .issueTime(Date.from(now.minus(1, ChronoUnit.HOURS)))
                                .issuer("https://dev.core.ipv.account.gov.uk")
                                .subject(UUID.randomUUID().toString())
                                .audience("https://address.cri.account.gov.uk")
                                .expirationTime(Date.from(now.plus(1, ChronoUnit.HOURS)))
                                .claim("response_type", "code")
                                .build());
        RSASSASigner rsaSigner = new RSASSASigner(getPrivateKey());
        signedJWT.sign(rsaSigner);

        SessionValidationException exception =
                assertThrows(
                        SessionValidationException.class,
                        () -> jwtVerifier.verifyAuthorizationJWT(clientConfigMap, signedJWT));

        assertEquals("JWT missing required claims: [nbf]", exception.getMessage());
    }

    private Map<String, String> getSSMClientConfig() {
        return Map.of(
                "issuer",
                "https://dev.core.ipv.account.gov.uk",
                "publicSigningJwkBase64",
                publicCertificate,
                "authenticationAlg",
                "RS256",
                "audience",
                "https://address.cri.account.gov.uk");
    }

    private Map<String, String> getECSSMClientConfig() {
        return Map.of(
                "issuer",
                "https://dev.core.ipv.account.gov.uk",
                "publicSigningJwkBase64",
                publicSigningJwkBase64,
                "authenticationAlg",
                "ES256",
                "audience",
                "https://address.cri.account.gov.uk");
    }

    private RSAPrivateKey getPrivateKey() throws InvalidKeySpecException, NoSuchAlgorithmException {
        return (RSAPrivateKey)
                KeyFactory.getInstance("RSA")
                        .generatePrivate(
                                new PKCS8EncodedKeySpec(Base64.getDecoder().decode(privateKey)));
    }

    private ECKey getECPrivateKey() throws ParseException {
        return ECKey.parse(new String(Base64.getDecoder().decode(privateSigningJwkBase64)));
    }
}
